import React from "react";

function CallFlowGraph({ data }) {

  if (!data || data.length === 0) return <div>No Call Flow</div>

  const nodePosition = {};
  const width = 800;
  const height = 50 + data.length * 70;
  const padding = 100;

  const nodes = Array.from (
    new Set(data.flatMap(pkt => [pkt.src_addr, pkt.dst_addr]))
  );
  const nodeX = nodes.reduce((acc, node, i) => {
    acc[node] = 100 + i * 200;
    return acc;
  }, {});

  nodes.forEach((node, idx) => {
    nodePosition[node] = padding + (width - 2 * padding) * (idx / (nodes.length - 1));
  });

  return (
    <svg width="100%" height={height} style={{ border: "1px solid #ccc" }}>
        {nodes.map((node, idx) => (
          <text
          key={node}
          x={nodePosition[node]}
          y={30}
          fontSize={16} // 노드 이름 크기
          fontWeight="bold"
          textAnchor="middle"
        >
          {node}
        </text>
        ))}

        {nodes.map((node, idx) => (
            <line
                key={node}
                x1={nodeX[node]}
                y1={50}
                x2={nodeX[node]}
                y2={height - 50}
                stroke="gray"
                strokeWidth={1}
            />
        ))}
        {data.map((pkt,idx) => {
            const y = 100 + idx * 30;
            return (
                       <g key={idx}>
            <line
              x1={nodeX[pkt.src_addr]}
              y1={y}
              x2={nodeX[pkt.dst_addr]}
              y2={y}
              stroke="black"
              strokeWidth={2}
              markerEnd="url(#arrow)"
            />
            <text
              x={(nodeX[pkt.src_addr] + nodeX[pkt.dst_addr]) / 2}
              y={y - 5}
              textAnchor="middle"
              fontSize="12"
            >
              {pkt.message}
            </text>
          </g>
            );
        })}


      {/* 화살표 마커 정의 */}
      <defs>
        <marker
          id="arrow"
          markerWidth="10"
          markerHeight="1"
          refX="10"
          refY="3"
          orient="auto"
        >
          <path d="M0,0 L0,6 L9,3 z" fill="black" />
        </marker>
      </defs>

        <text x="20" y="20">Call Flow Diagram</text>
    </svg>
  );
}

export default CallFlowGraph;
