import React from "react";

function CallFlowGraph({ data }) {
  if (!data || data.length == 0) return <div>No Call Flow</div>

  const width = 800;
  const height = 400;

  const nodes = Array.from (
    new Set(data.flatMap(pkt => [pkt.src_addr, pkt.dst_addr]))
  );
  const nodeX = nodes.reduce((acc, node, i) => {
    acc[node] = 100 + i * 200;
    return acc;
  }, {});

  return (
    <svg width="800" height="400" style={{ border: "1px solid #ccc" }}>
        {nodes.map(node => (
            <line
                key={node}
                x1={nodeX[node]}
                y1={50}
                x2={nodeX[node]}
                y2={height - 50}
                stroke="gray"
                strokeWidth={1}
            />
        ))}
        {data.map((pkt,idx) => {
            const y = 50 + idx * 30;
            return (
                       <g key={idx}>
            <line
              x1={nodeX[pkt.src_addr]}
              y1={y}
              x2={nodeX[pkt.dst_addr]}
              y2={y}
              stroke="black"
              strokeWidth={2}
              markerEnd="url(#arrow)"
            />
            <text
              x={(nodeX[pkt.src_addr] + nodeX[pkt.dst_addr]) / 2}
              y={y - 5}
              textAnchor="middle"
              fontSize="12"
            >
              {pkt.message}
            </text>
          </g>
            );
        })}


      {/* 화살표 마커 정의 */}
      <defs>
        <marker
          id="arrow"
          markerWidth="10"
          markerHeight="10"
          refX="10"
          refY="3"
          orient="auto"
        >
          <path d="M0,0 L0,6 L9,3 z" fill="black" />
        </marker>
      </defs>

        <text x="20" y="20">Call Flow Diagram</text>
    </svg>
  );
}

export default CallFlowGraph;
