// src/App.js
import React, { useState } from "react";
// import PacketTable from "./-PacketTable";
import {Modal, Button} from "react-bootstrap";


function PacketTable({ packets, currentFile }) {
  // console.log(packets.length);
  // if (!packets || packets.length === 0)
  //   return <p>No packets parsed yet.</p>;

    const [selectedPacket, setSelectedPacket] = useState(null);
  const [showModal, setShowModal] = useState(false);
  // const [currentFile, setCurrentFile] = useState(null);


  const handleShow = (pkt) => {
    setSelectedPacket(pkt);
    setShowModal(true);
  };

  const handleClose = () => {
    setShowModal(false);
    setSelectedPacket(null);
  };

  const fetchPacketDetail = async (id) => {
    if (!currentFile) {
      alert("No file selected!");
      return;
    }

    try {
      console.log("Current file name is: ", currentFile);
      console.log("Current id  is: ", id);

      const res = await fetch(
                    `/api/packet_detail?file=${encodeURIComponent(currentFile)}&id=${encodeURIComponent(id)}`
      );

      if (!res.ok) {
        const txt = await res.text();
        throw new Error(`Server ${res.status}: ${txt}`);
      }
      console.log(res)

      const data = await res.json();
      console.log(data)
      setSelectedPacket(data);
      setShowModal(true);
      // return res.json();
    }
    catch (err) {
      console.error(err);
      alert("Failed to fetch packet detail");
    }
  };

  // packets가 없으면 Modal 렌더링 자체를 하지 않음
  const shouldShowModal = showModal && selectedPacket !== null;

  return (
        <div className="container mt-4">

    <table className="table table-striped table-hover table-bordered mt-3">
      <thead className="table-dark">
        <tr>
          <th>ID</th>
          <th>Timestamp</th>
          <th>Source IP</th>
          <th>Destination IP</th>
          <th>Src Port</th>
          <th>Dst Port</th>
          <th>Protocol</th>
          <th>Description</th>
        </tr>
      </thead>
      <tbody>
        {packets && packets.length>0 ? (
          packets.map((pkt) => (
          <tr key={pkt.id}
              onClick={() =>
                // onSelect(pkt)
                fetchPacketDetail(pkt.id)
              }
              style={{ cursor: "pointer" }}>
            <td>{pkt.id}</td>
            <td>{pkt.ts}</td>
            <td>{pkt.src_ip}</td>
            <td>{pkt.dst_ip}</td>
            <td>{pkt.src_port}</td>
            <td>{pkt.dst_port}</td>
            <td>{pkt.protocol}</td>
            <td>{pkt.description}</td>
          </tr>
        ))
      ):(
        <tr>
          <td colSpan="8" className="text-center text-muted">
            No packets loaded
          </td>
          </tr>
      )}
      </tbody>
    </table>




      {/* ✅ Modal은 packets가 존재하고 선택된 패킷이 있을 때만 보여줌 */}
      {shouldShowModal && (
        <Modal show={shouldShowModal} onHide={handleClose} centered>
          <Modal.Header closeButton>
            <Modal.Title>Packet Details</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            {/*
            {selectedPacket && (
              <>
                <p><strong>ID:</strong> {selectedPacket.id}</p>
                <p><strong>Source IP:</strong> {selectedPacket.packet.ip.src_addr}</p>
                <p><strong>Destination IP:</strong> {selectedPacket.packet.ip.dst_addr}</p>
                <p><strong>L4 Src port:</strong> {selectedPacket.packet.l4.UDP.src_port}</p>
                <p><strong>L4 Dst port:</strong> {selectedPacket.packet.l4.UDP.dst_port}</p>
                <p><strong>GTP:</strong> {selectedPacket.packet.l4.UDP.dst_port}</p>
              </>
            )}
            */}
  {selectedPacket && (
    <div style={{ fontFamily: "monospace", fontSize: "14px" }}>

      {/* Packet ID */}
      <div className="mb-3">
        <h5 style={{ borderBottom: "1px solid #ccc", paddingBottom: "4px" }}>
          Packet #{selectedPacket.id}
        </h5>
      </div>

      {/* IP Section */}
      {/* <div className="card mb-3">
        <div className="card-header">
          <strong>IP Layer</strong>
        </div>
        <div className="card-body">
          <div style={{ marginLeft: "12px" }}>
            <p><strong>Source:</strong> {selectedPacket.packet.ip.src_addr}</p>
            <p><strong>Destination:</strong> {selectedPacket.packet.ip.dst_addr}</p>
          </div>
        </div>
      </div> */}
      {/* IP Header Diagram */}
<div className="ip-header-diagram" style={{ fontFamily: "monospace", fontSize: "12px", marginBottom: "20px" }}>
  <div className="row-label">0                   7 8                 15 16                23 24                31</div>

  <div className="ip-row">
    <div className="cell small">
      <div className="label">Version</div>
      <div>{selectedPacket.packet.ip.version}</div>
    </div>
    <div className="cell small">
      <div className="label">IHL</div>
      <div>{selectedPacket.packet.ip.ihl}</div>
    </div>
    <div className="cell small">
      <div className="label">DSCP</div>
      <div>{selectedPacket.packet.ip.dscp}</div>
    </div>
    <div className="cell small">
      <div className="label">ECN</div>
      <div>{selectedPacket.packet.ip.ecn}</div>
    </div>
    <div className="cell large">
      <div className="label">Total Length</div>
      <div>{selectedPacket.packet.ip.total_length}</div>
    </div>
  </div>

  <div className="ip-row">
    <div className="cell large">
      <div className="label">Identification</div>
      <div>{selectedPacket.packet.ip.identification}</div>
    </div>
    <div className="cell small">
      <div className="label">Flags</div>
      <div>{selectedPacket.packet.ip.flags}</div>
    </div>
    <div className="cell large">
      <div className="label">Fragment Offset</div>
      <div>{selectedPacket.packet.ip.frag_offset}</div>
    </div>
  </div>

  <div className="ip-row">
    <div className="cell small">
      <div className="label">TTL</div>
      <div>{selectedPacket.packet.ip.ttl}</div>
    </div>
    <div className="cell small">
      <div className="label">Protocol</div>
      <div>{selectedPacket.packet.ip.protocol}</div>
    </div>
    <div className="cell large">
      <div className="label">Header Checksum</div>
      <div>{selectedPacket.packet.ip.checksum}</div>
    </div>
  </div>

  <div className="ip-row">
    <div className="cell full">
      <div className="label">Source Address</div>
      <div>{selectedPacket.packet.ip.src_addr}</div>
    </div>
  </div>

  <div className="ip-row">
    <div className="cell full">
      <div className="label">Destination Address</div>
      <div>{selectedPacket.packet.ip.dst_addr}</div>
    </div>
  </div>

  {selectedPacket.packet.ip.options?.length > 0 && (
    <div className="ip-row">
      <div className="cell full">
        <div className="label">Options</div>
        <div>{JSON.stringify(selectedPacket.packet.ip.options)}</div>
      </div>
    </div>
  )}
</div>


      {/* L4 Section */}
      <div className="card mb-3">
        <div className="card-header">
          <strong>Layer 4 (Transport)</strong>
        </div>
        <div className="card-body">

          {"UDP" in selectedPacket.packet.l4 && (
            <div style={{ marginLeft: "12px" }}>
              <p><strong>Protocol:</strong> UDP</p>
              <p><strong>Src Port:</strong> {selectedPacket.packet.l4.UDP.src_port}</p>
              <p><strong>Dst Port:</strong> {selectedPacket.packet.l4.UDP.dst_port}</p>
            </div>
          )}

          {"TCP" in selectedPacket.packet.l4 && (
            <div style={{ marginLeft: "12px" }}>
              <p><strong>Protocol:</strong> TCP</p>
              <p><strong>Seq:</strong> {selectedPacket.packet.l4.TCP.seq}</p>
              <p><strong>Src Port:</strong> {selectedPacket.packet.l4.TCP.src_port}</p>
              <p><strong>Dst Port:</strong> {selectedPacket.packet.l4.TCP.dst_port}</p>
            </div>
          )}

        </div>
      </div>

      {/* Application Layer (GTP) */}
      <div className="card">
        <div className="card-header">
          <strong>Application Layer (GTP)</strong>
        </div>
        <div className="card-body">

          {"GTP" in selectedPacket.packet.app && (
            <div style={{ marginLeft: "12px" }}>
              <p><strong>Message Type:</strong> {selectedPacket.packet.app.GTP.msg_type}</p>
              <p><strong>Description:</strong> {selectedPacket.packet.app.GTP.msg_type_str}</p>
              <p><strong>TEID:</strong> {selectedPacket.packet.app.GTP.teid}</p>

              {/* IE List */}
              <div className="mt-3">
                <strong>Information Elements (IEs):</strong>
                <ul style={{ marginLeft: "20px" }}>
                  {selectedPacket.packet.app.GTP.ies.map((ie, index) => (
                    <li key={index}>
                      <strong>{ie.name}</strong> → {JSON.stringify(ie.value)}
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          )}

        </div>
      </div>
    </div>
  )}
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      )}
  </div>
  );
}

