import React from "react";

function CallFlowGraph({ data }) {

  if (!data || data.length === 0) return <div>No Call Flow</div>

  const width = 1000;
  const padding = 120;
  const rowHeight = 60;
  const headerHeight = 80;
  const height = headerHeight + data.length * rowHeight;


  const nodes = Array.from (
    new Set(data.flatMap(pkt => [pkt.src_addr, pkt.dst_addr]))
  );


  // üîπ ÎÖ∏Îìú X Ï¢åÌëú Í≥ÑÏÇ∞ (Îã® ÌïòÎÇòÎßå!)
  const nodeX = {};
  const span =
    nodes.length > 1
      ? (width - 2 * padding) / (nodes.length - 1)
      : 0;

  nodes.forEach((node, idx) => {
    nodeX[node] = padding + idx * span;
  });


  return (
    <svg width="100%" height={height} viewBox={`0 0 ${width} ${height}`} >
      {/* Title */}
      <text x="20" y="30" fontSize="18" fontWeight="bold">
        Call Flow Diagram
      </text>

      {nodes.map((node) => (
        <g key={node}>
          {/* ÌÖåÎëêÎ¶¨Î∞ïÏä§ */}
          <rect
            x={x - textWidth / 2 - paddingX}
            y={y - textHeight - paddingY}
            width={textWidth + paddingX * 2}
            height={textHeight + paddingY}
            rx={6}
            rx={6}
            fill="#f8f9fa"
            stroke="#333"
            strokeWidth={1}
          />
          <text
              key={node}
              x={nodeX[node]}
              y={60}
              fontSize={16} // ÎÖ∏Îìú Ïù¥Î¶Ñ ÌÅ¨Í∏∞
              fontWeight="bold"
              textAnchor="middle"
              style="border"
            >
            {node}
          </text>
          </g>
      ))}

      {/* ÏÑ∏Î°úÏ§Ñ */}
      {nodes.map((node, idx) => (
        <line
                key={node}
                x1={nodeX[node]}
                y1={50}
                x2={nodeX[node]}
                y2={height - 50}
                stroke="gray"
                // strokeDasharray="4,4"
                strokeWidth={2}
        />
      ))}
      {data.map((pkt,idx) => {
        const y = 100 + idx * 30;
        return (
          <g key={idx}>
            <line
              x1={nodeX[pkt.src_addr]}
              y1={y}
              x2={nodeX[pkt.dst_addr]}
              y2={y}
              stroke="black"
              strokeWidth={2}
              markerEnd="url(#arrow)"
            />

            <text
              x={(nodeX[pkt.src_addr] + nodeX[pkt.dst_addr]) / 2}
              y={y - 5}
              textAnchor="middle"
              fontSize="12"
            >
              {pkt.message}
            </text>
          </g>
        );
      })}


      {/* ÌôîÏÇ¥Ìëú ÎßàÏª§ Ï†ïÏùò */}
      <defs>
        <marker
          id="arrow"
          markerWidth="10"
          markerHeight="10"
          refX="10"
          refY="3"
          orient="auto"
        >
          <path d="M0,0 L0,6 L9,3 z" fill="black" />
        </marker>
      </defs>

    </svg>
  );
}

export default CallFlowGraph;
