import React from "react";

function CallFlowGraph({ data }) {
  if (!data || data.length === 0) return <div>No call flow data</div>;

  // 노드 추출 (unique IPs)
  const nodes = Array.from(
    new Set(data.flatMap(pkt => [pkt.src_ip, pkt.dst_ip]))
  );

  const nodePositions = {};
  const width = 800;  // 전체 폭
  const height = 50 + data.length * 70; // 메시지 수에 따라 높이
  const padding = 100;

  nodes.forEach((node, idx) => {
    nodePositions[node] = padding + (width - 2 * padding) * (idx / (nodes.length - 1));
  });

  return (
    <svg width="100%" height={height}>
      {/* 노드 라벨 */}
      {nodes.map((node, idx) => (
        <text
          key={node}
          x={nodePositions[node]}
          y={30}
          fontSize={16} // 노드 이름 크기
          fontWeight="bold"
          textAnchor="middle"
        >
          {node}
        </text>
      ))}

      {/* 메시지 화살표 */}
      {data.map((pkt, idx) => {
        const y = 60 + idx * 60;
        return (
          <g key={idx}>
            {/* 화살표 라인 */}
            <line
              x1={nodePositions[pkt.src_ip]}
              y1={y}
              x2={nodePositions[pkt.dst_ip]}
              y2={y}
              stroke="black"
              strokeWidth={2}
              markerEnd="url(#arrowhead)"
            />
            {/* 메시지 이름 */}
            <text
              x={(nodePositions[pkt.src_ip] + nodePositions[pkt.dst_ip]) / 2}
              y={y - 10}
              fontSize={14} // 메시지 이름 크게
              fontWeight="bold"
              textAnchor="middle"
            >
              {pkt.description}
            </text>
          </g>
        );
      })}

      {/* 화살표 마커 정의 */}
      <defs>
        <marker
          id="arrowhead"
          markerWidth="10"
          markerHeight="7"
          refX="10"
          refY="3.5"
          orient="auto"
        >
          <polygon points="0 0, 10 3.5, 0 7" fill="black" />
        </marker>
      </defs>
    </svg>
  );
}

export default CallFlowGraph;
