// import React, { useState } from "react";
import React, { useState, useRef, useEffect } from "react";


import "./ip.css";

function ComponentRenderer({ comp }) {
  if (!comp) return <div>None</div>;

   // comp가 string ("None")일 수도 있음
  if (typeof comp === "string") {
    return <div>{comp}</div>;
  }

  const type = Object.keys(comp)[0];   // "Ipv4Addr", "Protocol", ...
  const data = comp[type];

  // const type = Object.keys(comp)[0];
  // const data = comp[type];

  switch (type) {

    case "Ipv4Addr":
      return (
        <div>
          <div>IPv4 Addr: {comp.addr}</div>
          <div>Mask: {comp.mask}</div>
        </div>
      );

    case "Ipv6Addr":
      return (
        <div>
          <div>IPv6 Addr: {comp.addr}</div>
          <div>Mask: {comp.mask}</div>
        </div>
      );

    case "Protocol":
      return <div>Protocol: {comp.proto}</div>;

    case "SinglePort":
      return <div>Port: {comp.port}</div>;

    case "PortRange":
      return (
        <div>
          Port Range: {comp.start} - {comp.end}
        </div>
      );

    case "SecParamIdx":
      return <div>SPI: {comp.spi}</div>;

    case "TypeOfService":
      return (
        <div>
          ToS Value: {comp.value}, Mask: {comp.mask}
        </div>
      );

    case "FlowLabel":
      return <div>Flow Label: {comp.label}</div>;

    case "Unknown":
      return (
        <div>
          <i>Unknown Component</i>: type {comp.t}, raw = [{comp.data.join(", ")}]
        </div>
      );

    default:
      return <div>None</div>;
  }
}

function decodeBearerQoS(bytes, detail)
{
  if (!bytes || bytes.length < 2) {
    return "Invalid bytes"+(bytes.length);
  }

  let offset = 0;

  // 1바이트: PCI/PL/PVI
  const pci = (bytes[offset] & 0x40) !== 0;
  const pl = (bytes[offset] & 0x3c) >> 2;
  const pvi = (bytes[offset] & 0x01) !== 0;
  offset += 1;

  // 1바이트: QCI
  const qci = bytes[offset++];

  // 5바이트 숫자 → Number로 계산 (40bit까지 안전)
  function read5BytesNumber(start) {
    if (bytes.length < start + 5) return 0;
    return (
      (bytes[start] << 32) +
      (bytes[start + 1] << 24) +
      (bytes[start + 2] << 16) +
      (bytes[start + 3] << 8) +
      bytes[start + 4]
    );
  }

  const maxBitRateUL = read5BytesNumber(offset);
  offset += 5;

  const maxBitRateDL = read5BytesNumber(offset);
  offset += 5;

  const gbrUL = read5BytesNumber(offset);
  const gbrDL = read5BytesNumber(offset + 5);

  if (detail) {
    return (<td calSpan="4" style={{textAlign:"center"}}> PCI: ${pci}</td>);
  }
  else {
    return [
      `PCI: ${pci}`,
      `PL: ${pl}`,
      `PVI: ${pvi}`,
      `QCI: ${qci}`,
      `Max Bit Rate UL: ${maxBitRateUL} kbps`,
      `Max Bit Rate DL: ${maxBitRateDL} kbps`,
      `GBR UL: ${gbrUL} kbps`,
      `GBR DL: ${gbrDL} kbps`
    ].join("\n");
  }
}





function decodeEBI(bytes) {
  if (!bytes || bytes.length < 1) return "(empty)";
  return bytes[0] & 0x0f;   // lower 4 bits
}

function formatFTEID(f) {
  return [
    `V4: ${f.v4}`,
    `V6: ${f.v6}`,
    `Iface: ${f.iface_type}`,
    `TEID: ${f.teid}`,
    `IPv4: ${f.ipv4 ?? "none"}`,
    `IPv6: ${f.ipv6 ?? "none"}`
  ].join("\n");
}

function decodeAMBR(bytes) {
  let offset = 0;

  let uplink = 
    (((bytes[offset] << 24) |
    (bytes[offset+1] << 16) |
    (bytes[offset+2] << 8)  |
    bytes[offset+3] ) >>> 0);
  offset += 4;

  let downlink = 
    (((bytes[offset] << 24) |
    (bytes[offset+1] << 16) |
    (bytes[offset+2] << 8)  |
    bytes[offset+3] ) >>> 0);

    return [
      `AMBR Uplink: ${uplink}`,
      `AMBR Downlink: ${downlink}`
    ].join("\n");

}

function decodeFTEID(bytes) {
  let offset = 0;
  if (!bytes || bytes.length < 5) {
    throw new Error("F-TEID IE too short");
  }

  let v4 = (bytes[offset]&0x80) !== 0;
  let v6 = (bytes[offset]&0x40) !== 0;
  let iface_type = (bytes[offset]&0x3f);
  offset += 1;

  let teid = 
    (bytes[offset] << 24) |
    (bytes[offset+1] << 16) |
    (bytes[offset+2] << 8)  |
    (bytes[offset+3] );

  offset += 4;
  let ipv4 = null;
  let ipv6 = null;

  if (v4) {
    ipv4 = `${bytes[offset++]}.${bytes[offset++]}.${bytes[offset++]}.${bytes[offset++]}`;

  }
  if (v6) {
    const arr = bytes.slice(offset, offset + 16);
    offset += 16;

    // Convert to IPv6 string
    ipv6 = Array.from(new Uint8Array(arr))
      .map((b, i) => (i % 2 === 0 ? (b << 8) | arr[i + 1] : null))
      .filter(v => v !== null)
      .map(v => v.toString(16))
      .join(":");
  }

  return {
    v4,
    v6,
    iface_type,
    teid: teid >>> 0,  // unsigned
    ipv4,
    ipv6
  };

}

function decodeAPN(bytes) {
  if (!bytes || bytes.length === 0) return "(empty)";

  // bytes → 문자열
  return bytes.map(b => String.fromCharCode(b)).join("");
}

function hexToBytes(hex) {
  if (hex.length % 2 !== 0) throw new Error("Invalid hex string");
  const bytes = new Uint8Array(hex.length / 2);
  for (let i = 0; i < hex.length; i += 2) {
    bytes[i / 2] = parseInt(hex.substr(i, 2), 16);
  }
  return bytes;
}

// IMSI BCD decode
function decodeBCD(input) {
  let bytes;

  // hex string이 들어온 경우
  if (typeof input === "string") {
    bytes = hexToBytes(input);
  } 
  // Uint8Array가 들어온 경우
  else {
    bytes = input;
  }

  let digits = [];

  for (let b of bytes) {
    const low = b & 0x0f;
    const high = (b & 0xf0) >> 4;

    if (low <= 9) digits.push(low);
    // high nibble가 0xF이면 끝을 의미하므로 break
    if (high <= 9) digits.push(high);
  }

  return digits.join("");
}

function GtpHexDump({ raw }) {
  if (!raw || raw.length === 0) return <div>No raw data</div>;

  const toHex = (n) => n.toString(16).padStart(2, "0").toUpperCase();
  const toAscii = (n) =>
    n >= 0x20 && n <= 0x7E ? String.fromCharCode(n) : ".";

  // 16 bytes per line
  const lines = [];
  for (let i = 0; i < raw.length; i += 16) {
    const chunk = raw.slice(i, i + 16);

    const offset = i.toString(16).padStart(4, "0");

    // 16바이트를 두 그룹으로 나누기
    const first8 = chunk.slice(0, 8).map(toHex).join(" ");
    const last8 = chunk.slice(8, 16).map(toHex).join(" ");
    const hexBytes = first8 + "  " + last8; // 두 그룹 사이에 여분의 공백

    // const hexBytes = chunk
    //   .map((b) => toHex(b))
    //   .join(" ");

    const ascii = chunk
      .map((b) => toAscii(b))
      .join("");

    lines.push({ offset, hexBytes, ascii });
  }

  return (
    <pre
      style={{
        background: "#111a23",
        color: "#eee",
        padding: "10px",
        borderRadius: "6px",
        fontFamily: "monospace",
        fontSize: "13px",
        overflowX: "auto"
      }}
    >
      {lines.map((line, idx) => (
        <div key={idx}>
          {line.offset}  {line.hexBytes.padEnd(47)}  {line.ascii}
        </div>
      ))}
    </pre>
  );
}

function GtpIeSimpleViewer({ ies }) {
  const [hoveredRaw, setHoveredRaw] = React.useState(null);
  return (
    <div>
       <GtpIeSimpleTable ies={ies} onHoverRaw={setHoveredRaw} />
    </div>
  );
}

function GtpIeViewer({ ies }) {
  return (
    <div>
      <GtpIeTable ies={ies} />
    </div>
  );
}

function renderIeValue(value) {
  if (!value) return "(none)";

  const type = Object.keys(value)[0];
  const data = value[type];

  switch (type) {
    case "Raw":
      return (
        <span>
          {/* 0x{value.data.map((b) => b.toString(16).padStart(2, "0")).join("")} */}
          0x{data.map(b => b.toString(16).padStart(2, "0")).join("")}
        </span>
      );

    case "Uint8":
    case "Uint16":
    case "Uint32":
      return <span>{data}</span>;

    case "Utf8String":
    case "Apn":
      return <span>{data}</span>;

    case "Ipv4":
    case "Ipv6":
      return <span>{data}</span>;

    case "Timer":
      return (
        <span>
          unit={data.unit}, value={data.value}
        </span>
      );

    case "Ambr":
      return (
        <div>
          <div>UL: {data.ul}</div>
          <div>DL: {data.dl}</div>
        </div>
      );

    case "UserLocationInfo":
      // data = UliValue 구조체
      const uli = data;

      return (
        <div style={{ padding: "4px 0" }}>
          {uli.has_tai && uli.tai && (
            <div style={{ marginBottom: "6px" }}>
              <b>TAI</b><br />
              MCC: {uli.tai.mcc}<br />
              MNC: {uli.tai.mnc}<br />
              TAC: {uli.tai.tac}
            </div>
          )}

          {uli.has_ecgi && uli.ecgi && (
            <div style={{ marginBottom: "6px" }}>
              <b>ECGI</b><br />
              MCC: {uli.ecgi.mcc}<br />
              MNC: {uli.ecgi.mnc}<br />
              ECI: {uli.ecgi.eci}
            </div>
          )}

          {uli.has_lai && uli.lai && (
            <div style={{ marginBottom: "6px" }}>
              <b>LAI</b><br />
              MCC: {uli.lai.mcc}<br />
              MNC: {uli.lai.mnc}<br />
              LAC: {uli.lai.lac}
            </div>
          )}

          {uli.has_rai && uli.rai && (
            <div style={{ marginBottom: "6px" }}>
              <b>RAI</b><br />
              MCC: {uli.rai.mcc}<br />
              MNC: {uli.rai.mnc}<br />
              RAC: {uli.rai.rac}
            </div>
          )}

          {uli.has_sai && uli.sai && (
            <div style={{ marginBottom: "6px" }}>
              <b>SAI</b><br />
              MCC: {uli.sai.mcc}<br />
              MNC: {uli.sai.mnc}<br />
              SAC: {uli.sai.sac}
            </div>
          )}

          {uli.has_cgi && uli.cgi && (
            <div style={{ marginBottom: "6px" }}>
              <b>CGI</b><br />
              MCC: {uli.cgi.mcc}<br />
              MNC: {uli.cgi.mnc}<br />
              CI: {uli.cgi.ci}
            </div>
          )}

          {/* 아무것도 없으면 빈 값 표시 */}
          {!uli.has_tai &&
            !uli.has_ecgi &&
            !uli.has_lai &&
            !uli.has_rai &&
            !uli.has_sai &&
            !uli.has_cgi && <span>(empty ULI)</span>}
        </div>
      );



    case "FTeid":
      return (
        <div>
          <div>TEID: 0x{data.teid.toString(16)}</div>
          {data.v4 &&
            <div>IPv4 Address: {data.ipv4}</div>
          }
          {data.v6 &&
            <div>IPv6 Address: {data.ipv6}</div>
          }
          <div>Interface Type: {data.iface_type}</div>
        </div>
      );

    case "BearerTFT":
      return (
        <div style={{ marginLeft: "10px" }}>
          <h5>Bearer TFT</h5>

          <div><b>TFT Operation Code:</b> {data.tft_op_code} ({data.str_tft_op_code})</div>
          <div><b>E-Bit:</b> {data.e_bit ? "True" : "False"}</div>
          <div><b>Number of Packet Filters:</b> {data.num_filter}</div>

          <hr />

      {data.packet_filter_list?.map((pf, pfIdx) => (
        <div key={`pf-${pfIdx}`} style={{ marginTop: "10px", padding: "10px", border: "1px solid #ccc" }}>
          <h6>Packet Filter #{pfIdx + 1}</h6>

          <div><b>Direction:</b> {pf.pf_dir}</div>
          <div><b>Filter ID:</b> {pf.pf_id}</div>
          <div><b>Precedence:</b> {pf.pkt_prec}</div>
          <div><b>PF Length:</b> {pf.pf_len}</div>

          <div style={{ marginTop: "10px" }}>
            <b>Components:</b>
            <ul>
              {pf.packet_filter_component_list?.map((comp, cidx) => (
                <li key={`comp-${cidx}`} style={{ marginTop: "5px" }}>
                  <div><b>Type ID:</b> 0x{comp.pf_type_id.toString(16)}</div>

                  {/* Component Details */}
                  <ComponentRenderer comp={comp.components} />
                </li>
              ))}
            </ul>
          </div>
        </div>
      ))}
    </div>
      );

    case "ServingNetwork":
      return (
        <div>
          <div>MCC: {data.mcc}</div>
          <div>MNC: {data.mnc}</div>
        </div>
      );

    case "BearerQoS":
      return (
        <div>
          <div>QCI: {data.qci}</div>
          <div>Max UL: {data.max_ul}</div>
          <div>Max DL: {data.max_dl}</div>
          <div>Guaranteed UL: {data.gbr_ul}</div>
          <div>Guaranteed DL: {data.gbr_dl}</div>
        </div>
      );

    case "UserLocationInfo":
      return (
        <div>
          <div>TAI: {data.tai}</div>
          <div>ECGI: {data.ecgi}</div>
          <div>RAI: {data.rai}</div>
        </div>
      );

    case "SubIeList":
      return (
        <div style={{ marginLeft: "8px", borderLeft: "2px solid #ccc", paddingLeft: "8px" }}>
          {data.map((sub, idx) => (
            <div key={idx} style={{ marginBottom: "6px" }}>
              <strong>{sub.type_str}</strong>
              <div>{renderIeValue(sub.ie_value)}</div>
            </div>
          ))}
        </div>
      );

    case "None":
      return <span>(none)</span>;

    default:
      return <span>(unknown type)</span>;
  }
}

// function IeViewer({ ies, onHoverRaw = () => {}  }) {
//   const [hoveredRaw, setHoveredRaw] = React.useState(null);

//   return (
//     <div style={{ display: "flex", gap: "12px" }}>
//       <div style={{ flex: 1 }}>
